package com.example.homework011

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.LayoutInflater
import android.widget.Toast
import com.bumptech.glide.Glide
import com.example.homework011.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    lateinit var binding: ActivityMainBinding
    val url = "data:image/jpeg;base64,"
    val url1 = "data:image/jpeg;base64,"
    val url2 = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYFE_7OgQiQEp2D6Gir0M8ZmWwcNA1o-emSA&usqp=CAU"
    var nameAnimal: String? = null

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)
        initClicker()

    }


    private fun initClicker(){
        var image = binding.ivAnimal
        binding.btnCat.setOnClickListener {
            Glide.with(this ).load(url1).into(image)
            nameAnimal = "cat"
        }
        binding.btnDog.setOnClickListener{
            Glide.with(this).load(url).into(image)
            nameAnimal = "dog"
        }
        binding.btnGiraffe.setOnClickListener {
            Glide.with(this).load(url2).into(image)
            nameAnimal = "giraffe"
        }
        binding.btnThatDog.setOnClickListener {
            if (nameAnimal == "dog"){
                Toast.makeText(this, "правильно", Toast.LENGTH_SHORT).show()
            }else Toast.makeText(this, "не правильно", Toast.LENGTH_SHORT).show()
        }
        binding.btnThatCat.setOnClickListener {
            if (nameAnimal == "cat"){
                Toast.makeText(this, "правильно", Toast.LENGTH_SHORT).show()
            }else Toast.makeText(this, "не правильно", Toast.LENGTH_SHORT).show()
        }
        binding.btnThatGiraffe.setOnClickListener {
            if (nameAnimal == "giraffe"){
                Toast.makeText(this, "правильно", Toast.LENGTH_SHORT).show()
            }else Toast.makeText(this, "не правильно", Toast.LENGTH_SHORT).show()
        }
        binding.btnResult.setOnClickListener {
            var intent:Intent = Intent(this@MainActivity, ResultActivity::class.java)
            intent.putExtra("key", nameAnimal)
            startActivity(intent)
        }


    }
}